# Max 32 parameters

template '2010-09-09' do
  parameter 'StringParam', 'String'
  parameter 'NumberParam', 'Number'
  parameter 'ListParam', 'CommaDelimitedList'

  parameter 'DescriptionParam', String do
    description 'P'
  end

  parameter 'LongDescription', :String do
    description ''
  end

  parameter 'ConstraintDescription1', 'String' do
    constraint 'A'
  end

  parameter 'LongConstraintDescription1', 'string' do
    description 'param description'
    constraint ''
  end

  parameter 'Pattern1', 'String' do
    pattern '\d+'
  end

  parameter 'Values1', :String do
    values 'a', 'b', 'c'
    values ['e', 'f']
  end

  
  
  # At least one resource is required
  resource 'Dummy', 'AWS::CloudFormation::WaitConditionHandle'
end
